class Solution {
    // Depth-First Search function to traverse the connected components
    void dfs(int node, const vector<vector<int>> &adjacencyList, vector<bool> &visited, int &componentSize, int &totalEdges) {
        // Check if the current node has already been visited
        if (visited[node]) {
            return; // If visited, no need to process it again
        }

        visited[node] = true; // Mark the current node as visited
        ++componentSize; // Increment the size of the current connected component
        totalEdges += adjacencyList[node].size(); // Count the total number of edges connected to this component

        // Recursively visit all connected nodes in the component
        for (int neighbor : adjacencyList[node]) {
            // Call dfs recursively for each neighboring node to explore the component
            dfs(neighbor, adjacencyList, visited, componentSize, totalEdges);
        }
    }

public:
    // Function to count the number of complete connected components in a graph
    int countCompleteComponents(int n, vector<vector<int>>& edges) {
        // Create an adjacency list to represent the graph
        vector<vector<int>> adjacencyList(n);
        for (const auto& edge : edges) {
            adjacencyList[edge[0]].push_back(edge[1]);
            adjacencyList[edge[1]].push_back(edge[0]);
        }

        vector<bool> visited(n); // Create a boolean array to track visited nodes
        int completeComponents = 0; // Initialize the count of complete connected components

        // Traverse the graph and count complete components
        for (int i = 0; i < n; ++i) {
            if (!visited[i]) {
                int componentSize = 0; // Initialize the size of the current component
                int totalEdges = 0; // Initialize the total edges connected to the component
                dfs(i, adjacencyList, visited, componentSize, totalEdges);

                // Check if the component is complete (meets the condition)
                if (componentSize * (componentSize - 1) == totalEdges) {
                    ++completeComponents; // If complete, increment the count of complete components
                }
            }
        }
        return completeComponents; // Return the total count of complete components in the graph
    }
};

