class Solution:
    def countCompleteComponents(self, n: int, edges: List[List[int]]) -> int:
        """
        count the edges in each component.
        if number of edges == n * (n-1) / 2 then
        it's a complete connected component else not
        """
        edge_map = {i: set() for i in range(n)}
        for edge in edges:
            edge_map[edge[0]].add(edge[1])
            edge_map[edge[1]].add(edge[0])

        def dfs(node, dfs_visited):
            dfs_visited.add(node)
            for nei in edge_map[node]:
                if nei not in dfs_visited:
                    dfs(nei, dfs_visited)

        self.comp_map = {}
        self.comp_count_map = {}
        comp_count = 0
        for i in range(n):
            if i not in self.comp_map:
                dfs_visited = set()
                dfs(i, dfs_visited)
                for node in dfs_visited:
                    self.comp_map[node] = comp_count
                self.comp_count_map[comp_count] = len(dfs_visited)
                comp_count+=1
        self.edge_map = collections.defaultdict(int)
        for edge in edges:
            self.edge_map[self.comp_map[edge[0]]]+=1

        # if there are nodes with zero edges then they won't be present in
        # edge map. Those are actually complete connected components, hence 
        # we will add that to answer towards the end

        complete_count = 0
        for key in self.edge_map:
            edge_cnt = self.edge_map[key]
            node_cnt = self.comp_count_map[key]
            if node_cnt * (node_cnt-1)/2 == edge_cnt:
                complete_count+=1

        #print(comp_count)
        #print(self.comp_count_map)
        #print(self.comp_map)
        #print(self.edge_map)
        return complete_count + len(self.comp_count_map) - len(self.edge_map)
