class Solution {

    Map<Integer, List<Integer>> m = new HashMap();
    List<Set> connectedComponents = new ArrayList();
    boolean[] processed;
    public int countCompleteComponents(int n, int[][] edges) {
        processed = new boolean[n];
        for (int i = 0; i < n; i++) {
            m.put(i, new ArrayList());
        }

        for (int i = 0; i < edges.length; i++) {
            m.get(edges[i][0]).add(edges[i][1]);
            m.get(edges[i][1]).add(edges[i][0]);
        }

        for (int i = 0; i < n; i++) {
            if (!processed[i])
            connectedComponents.add(helper(i, new HashSet()));
        }

        int completeConnectedCount = 0;
        for (Set<Integer> graph : connectedComponents) {
            int numNodes = graph.size();
            boolean isComplete = true;
            for (int node : graph) {
                if (m.get(node).size() != numNodes - 1) {
                    isComplete = false;
                    break;
                };
            }
            if (isComplete) completeConnectedCount++;
        }


        return completeConnectedCount;
    }

    private Set<Integer> helper(int vertex, Set<Integer> set) {
        processed[vertex] = true;
        set.add(vertex);
        List<Integer> connections = m.get(vertex);
        for (int connection : connections) {
            if (!processed[connection]) {
                helper(connection, set);
            }
        }
        return set;
    }
}

