class Solution {
    /* Step 1: construct a graph
    Step 2: applying dfs on graph to find number of components & no. of nodes in each subgraphs
    Step 3: comparing no.of edges with no. of edges needed for CCC */

    public void dfs(ArrayList<ArrayList<Integer>> graph, boolean visited[], int curr, int em[]) {
        visited[curr] = true; // marking visited

        for(int nbr: graph.get(curr)) {
            if(!visited[nbr]) {
                dfs(graph, visited, nbr, em); // traversing to next node if not visited
                em[0]++;
            }
            em[1]++;
        }
    }
    public int countCompleteComponents(int n, int[][] edges) {
        ArrayList<ArrayList<Integer>> graph = new ArrayList<>(); // graph schema

        for(int i = 0; i < n; i++) 
            graph.add(new ArrayList<Integer>());

        for(int edge[]: edges) { // constructing graph
            int u = edge[0];
            int v = edge[1];

            graph.get(u).add(v);
            graph.get(v).add(u);
        }

        boolean visited[] = new boolean[n]; // to track position while traversing
        int components = 0; // count of all components
        int em[] = new int[2]; // to keep count of edges & nodes
        em[0] = 1; // starting point is a node

        for(int i = 0; i < n; i++) 
            if(!visited[i]) {
                dfs(graph, visited, i, em); // applying dfs to traverse through graph
                if(em[0]*(em[0]-1)/2 == em[1]/2) components++; // checking is subgraph a CCC
                em[0] = 1; // updating for new subgraph
                em[1] = 0; // updating for new subgraph
            } 

        return components;
    }
}
