    void dfs(int i , int parent , int &nodeCount , int &edgeCount , vector<vector<bool>> &edgeVisited , vector<bool> &visited , vector<vector<int>> &adj)
    {
        if((parent != -1 && edgeVisited[i][parent])) return;
