        for(int nex: graph[curr]){
            if(!visited[nex]){
                dfs(graph,nex,visited,node,edge_c);
            }
        }
    }
    int countCompleteComponents(int n, vector<vector<int>>& edges) {
        vector<vector<int>> graph(n);
