class Solution:
    def countCompleteComponents(self, n: int, edges: List[List[int]]) -> int:
        from collections import deque,defaultdict
        adj = defaultdict(list)
        visited = [False]*(n)
        count = 0
        for i in edges:
            adj[i[0]]+=[i[1]]
            adj[i[1]]+=[i[0]]
        for i in range(n):
            v = 0
            ce = 0
            if not visited[i]:
                q = deque()
                q.append(i)
                visited[i] = True
                while q:

                    node = q.popleft()
                    v+=1
                    ce+=len(adj[node])
                    for x in adj[node]:
                        if not visited[x]:
                            q.append(x)
                            visited[x] = True
                if ce == v*(v-1):
                    count+=1
        return count
