class Solution {
public:
    void dfs(vector<int> adj[], int node, vector<int>&vis, int& V, int& E){
        vis[node]=1;
        V++;
        for(auto u :adj[node]){
            E++;
            if(!vis[u]) dfs(adj,u,vis,V,E);
        }

    }
    int countCompleteComponents(int n, vector<vector<int>>& edges) {
        int ans = 0;

        vector<int> adj[n];
        for(auto e : edges){
            adj[e[0]].push_back(e[1]);
            adj[e[1]].push_back(e[0]);
        }
        vector<int> vis(n,0);
        for(int i=0;i<n;i++){
            if(!vis[i]){
                int V=0, E=0;
                dfs(adj,i, vis, V, E);

                if(E == V*(V-1)) ans++;
            }
        }
        return ans;
    }
};
