class Solution {
public:
    void dfs( int node, int & size, vector<int> adj[],  vector<int> & vis, vector<int> & all ){
        vis[node] = 1;
        size ++;
        all.push_back(node);

        for( auto it : adj[node] ){
            if( vis[it] == 0 ) dfs(it, size, adj,vis,all);
        }
    }

    int countCompleteComponents(int n, vector<vector<int>>& edges) {

        vector<int> adj[n]; // adjacency array
        vector<int> vis(n,0); // visited array

        for( int i = 0; i<edges.size(); i++ ){ // creating the adjacency array
            vector<int> temp = edges[i];
            adj[temp[0]].push_back(temp[1]);
            adj[temp[1]].push_back(temp[0]);
        }


        int ans = 0; // to keep a count of the number of complete components

        // so
        // we need to find how many complete comoponents are there in the given graph system
        // and a complete component means all the nodes in that component should be connected to every other node in that component
        // means we need to go to a component and check the size of adjacency list of all the nodes in the array
        // if, the size of the adjacency list of all the nodes in that component is 1 less that the number of nodes means all the nodes are connected to all the other ones, and hence its a complete component
        // increase the ans by 1 and repeat

        for( int i = 0; i<n; i++ ){ 
            if( vis[i] == 0 ) {
                // call dfs -> this function will traverse to all the nodes in a component
                int size = 0; // to store the number of nodes in that component
                vector<int> all; // we need to know who all nodes are there in that component so that we can check if its connected to all others
                dfs(i, size, adj,vis, all);
                bool check = true; // to check if "ALL" the nodes fulfill the requirement
                for( int j = 0; j<all.size(); j++ ){
                    vector<int> node = adj[all[j]];
                    if( node.size() < size-1 ) check = false;
                }
                if( check == true ) ans++;
            }
        }

        return ans;
    }
};
