class Solution {
public:
    pair<int,int>solve(int node,vector<int>&visited,vector<vector<int>>&adj){
    //base case    
    //-->this will never happen as we're alreday checking visited in for loop
        if(visited[node])return {0,0};

        visited[node]=1;
        pair<int,int>ans={1,0};

        for(auto it:adj[node]){
            if(!visited[it]){
                pair<int,int> temp = solve(it,visited,adj);
                ans.first+= temp.first;
                ans.second+= 1 + temp.second;
            }
            else
                ans.second+=1;
        }
        return ans; // {total_nodes in components,2* toltal edges in component}
    }

    int countCompleteComponents(int n, vector<vector<int>>& edges) {
        vector<int>visited(n,0);
        vector<vector<int>>adj(n);
        int components=0;

        for(auto it:edges){
            adj[it[0]].push_back(it[1]);
            adj[it[1]].push_back(it[0]);
        }

        for(int node=0;node<n;node++){
            if(!visited[node]){
                pair<int,int> ans = solve(node,visited,adj);
                int total_nodes = ans.first;
                int total_edges = ans.second/2;
                if((total_nodes*(total_nodes-1))/2==total_edges)
                    components++;
            }
        }
        return components;
    }
};

