class Solution {
    private:
    pair<int,int> DFS(int node,vector<int>adj[],vector<int>&vis,int par){
        vis[node] = 1;
        int nCnt = 1;
        int edgCnt = 0;
        for(auto it:adj[node]){
            if(vis[it] == 0){
                edgCnt++;
                auto pr = DFS(it,adj,vis,node);
                vis[it]=-1;
                edgCnt+= pr.second;
                nCnt+= pr.first;
            }
            else if(it!=par && vis[it]!=-1){
                edgCnt++;
            }
        }
        return {nCnt,edgCnt};
    }

    int calNC2(int n){
        return n*(n-1)/2;
    }
public:
    int countCompleteComponents(int n, vector<vector<int>>& edges) {
        vector<int> adj[n];
        for(auto it:edges){
            adj[it[0]].push_back(it[1]);
            adj[it[1]].push_back(it[0]);
        }
        vector<int> vis(n,0);
        int cnt = 0;
        for(int i=0;i<n;i++){
            if(vis[i]==0){
                auto pr = DFS(i,adj,vis,-1);
                int nc2 = calNC2(pr.first);
                if(nc2 == pr.second)cnt++;
            }
        }

        return cnt;
    }
};
