    vector<int> G[100];
    int vis[1000];
    void dfs(int u, int &p, int &q) {
        if(vis[u]) return;
        vis[u] = 1;
        p++;
        q += G[u].size();
        for(auto &x : G[u]) {
            if(x != u) dfs(x, p, q);
