class Solution {
public:
    int countCompleteComponents(int n, vector<vector<int>>& edges) {
        vector<bool> visit(n,false);
        if(edges.size()==0)return n;
        vector<vector<int>> adj(n);
        for(auto e: edges){
            adj[e[1]].push_back(e[0]);
            adj[e[0]].push_back(e[1]);
        }
        int nas=0;
        queue<int>q;
        for(int i=0;i<n;i++){
            if(visit[i]==false){
                visit[i]=true;
                q.push(i);
            }
            else continue;
            int count=0;
            int edges=0;
            while(!q.empty()){
                int n=q.front();
                q.pop();
                count++;
                for(int e: adj[n]){
                    edges++;
                    if(visit[e]==false){
                        visit[e]=true;
                        q.push(e);
                    }
                }

        }
        edges/=2;
            if(edges == (count-1)*count/2)nas++;
        }
        return nas;
    }
};
