class Solution {
private:
    void bfs(int node , vector<int> adj[] , vector<int> &vis , int &nodes , int &edges){
        queue<int> q;
        q.push(node);
        vis[node] = 1;
        int cnt = 0;

        while(!q.empty()){
            int f = q.front();
            q.pop();

            for(int ele : adj[f]){
                if(!vis[ele]){
                    nodes++;
                    q.push(ele);
                    vis[ele] = 1;
                }
                cnt++;
            }
        }
        edges = cnt/2;
    }
public:
    int countCompleteComponents(int n, vector<vector<int>>& edges) {
        vector<int> adj[n];
        for(auto ele : edges){
            adj[ele[0]].push_back(ele[1]);
            adj[ele[1]].push_back(ele[0]);
        }

        vector<int> vis(n , 0);

        int ans = 0;

        for(int i=0 ; i<n ; i++){
            if(!vis[i]){
                int nodes = 1 , edges = 0;
                bfs(i , adj , vis , nodes , edges);

                if( edges == (nodes*(nodes-1))/2 ) ans++;
            }
        }
        return ans;
    }
};
```

