    void dfs(int node,int& edge,int& v,vector<int>& vis,vector<vector<int>>& adjList){
        v++;
        vis[node]=1;
        for(auto it:adjList[node]){
            edge++;
            if(vis[it]==0){
                dfs(it,edge,v,vis,adjList);
            }
        }
