class Solution {
    public int countCompleteComponents(int n, int[][] edges) {
        ArrayList<ArrayList<Integer>> adj=new ArrayList<>();
        for(int i=0;i<n;i++)
        {
            adj.add(new ArrayList<>());
        }
        int deg[]=new int[n];
        for(int i=0;i<edges.length;i++)
        {
            adj.get(edges[i][0]).add(edges[i][1]);
            adj.get(edges[i][1]).add(edges[i][0]);
            deg[edges[i][0]]++;
            deg[edges[i][1]]++;
        }
        int vis[]=new int[n];
        int res=0;
        for(int i=0;i<n;i++){
            if(vis[i]==0)
            {
                Set<Integer> hs=new HashSet<>();
                dfs(i,adj,vis,hs);
                int size=hs.size();
                int temp=0;
                for(int it:hs)
                {
                    if(size-1==deg[it])
                    {
                        temp++;
                    }
                    else
                    {
                        break;
                    }
                }
                if(temp==size)
                {
                    res++;
                }
            }
        }
        return res;
    }
    public void dfs(int s,ArrayList<ArrayList<Integer>> adj,int[] vis,Set<Integer> hs)
    {
        vis[s]=1;
        hs.add(s);
        for(int it:adj.get(s))
        {
            if(vis[it]==0)
            {
                dfs(it,adj,vis,hs);
            }
        }
        return ;
    }
}
