class Solution:
  def __init__(self):
    self.mark = []
    self.g = [] 
    self.eds = 0 
    self.nodes = 0 

  def dfs(self, x:int, degree:int):
    self.mark[x] = True 
    self.nodes += 1 
    for i in self.g[x]:
      if not self.mark[i]:
        self.dfs(i,degree) 
      self.eds+=1

  def countCompleteComponents(self, n: int, edges: List[List[int]]) -> int:
    self.g = [[] for _ in range(n)] 
    self.mark = [False  for _ in range(n)] 
    ans = 0 
    for i in edges: 
      x = i[0]
      y = i[1] 
      self.g[x].append(y)
      self.g[y].append(x) 

    for i in range(0,n):
      if not self.mark[i]:
        self.eds = 0 
        self.nodes = 0 
        self.dfs(i, len(self.g[i])) 
        if (self.nodes - 1)*self.nodes == self.eds:
          ans+=1

    return ans
```

