class Solution {
public:
    void dfs(int src, vector<vector<int>>& adj, vector<int>& vis, int& nodes, int& edges){
        vis[src] = 1;
        nodes++;
        for(auto it: adj[src]){
            edges++;
            if(!vis[it]){
                dfs(it, adj, vis, nodes, edges);
            }
        }
    }
    int countCompleteComponents(int n, vector<vector<int>>& edges) {
        int ans = 0;
        vector<int> vis(n, 0);
        vector<vector<int>> adj(n);
        for(int i=0; i<edges.size(); i++){
            adj[edges[i][0]].push_back(edges[i][1]);
            adj[edges[i][1]].push_back(edges[i][0]);
        }
        int nodes = 0, eges = 0;
        for(int i=0; i<n; i++){
            if(!vis[i]){
                dfs(i, adj, vis, nodes, eges);
                cout<<nodes<<" "<<eges<<endl;
                if(eges >= nodes*(nodes-1)) ans++;
                nodes = 0;
                eges = 0;
            }
        }
        return ans;
    }
};
