class Solution {
public:
    void dfs(int s, vector<vector<int>>& g, vector<bool>& visited){
        if(!visited[s]){
            visited[s] = true;
            for(int i = 0; i < g[s].size(); ++i)
                dfs( g[s][i], g, visited);
        }
    }

    int countCompleteComponents(int n, vector<vector<int>>& edges) {
        vector<vector<int>> g(n);
        for(auto edge : edges){
            g[edge[0]].push_back(edge[1]);
            g[edge[1]].push_back(edge[0]);
        }
        int count = 0;
        set<int> s;
        vector<bool> visited(n, false);
        for(int i = 0; i < n; ++i){
            if(!visited[i]){
                // cout << i << " \n";
                // visited[i] = true;
                s.insert(i);
                for(int j = 0; j < g[i].size(); ++j){
                    s.insert(g[i][j]);
                }
                bool flag = true;
                for(auto k = s.begin(); k != s.end(); ++k){
                    if(!((s.size()-1 == g[*k].size()) && g[*k].size() == g[i].size())){
                        flag = false;
                        break;
                    }
                    for(int x = 0; x < g[*k].size(); ++x){
                        if(s.find(g[*k][x]) == s.end()){
                            flag = false;
                            break;
                        } 
                    }
                }
                if(flag){
                    count++;
                }
                dfs(i, g, visited);
                s.clear();
            }
        }
        return count;
    }
};
