        }
        else if(rank.get(ulp_v)>rank.get(ulp_u)) {
            parent.set(ulp_u,ulp_v);
        }
        else {
            parent.set(ulp_v,ulp_u);
            int rankU=rank.get(ulp_u);
            rank.set(ulp_u,rankU+1);
        }
    }
}
class Solution {
    public int countCompleteComponents(int n, int[][] edges) {

        // T.C. -> O(n*n+edges.length+n+n)
        // A.S. -> O(n*n+3*n+n)

        int[][] adjm=new int[n][n];
        for(int i=0;i<=n-1;++i) {
            for(int j=0;j<=n-1;++j) {
                adjm[i][j]=0;
            }
        }
        int m=edges.length;
        DisjointSet ds=new DisjointSet(n);
        for(int i=0;i<=m-1;++i) {
            int u=edges[i][0];
            int v=edges[i][1];
            adjm[u][v]=1;
            adjm[v][u]=1;
            int ulp_u=ds.findUPar(u);
            int ulp_v=ds.findUPar(v);
            if(ulp_u!=ulp_v) {
                ds.unionByRank(u,v);
            }
        }
        Map<Integer,List<Integer>> mp=new HashMap<>();
        for(int i=0;i<=n-1;++i) {
            int ulp_i=ds.findUPar(i);
            if(mp.containsKey(ulp_i)!=true) {
                List<Integer> l=new ArrayList<>();
                l.add(i);
                mp.put(ulp_i,l);
            }
            else {
                List<Integer> l=mp.get(ulp_i);
                l.add(i);
                mp.put(ulp_i,l);
            }
        }
        int ans=0;
        for(Map.Entry<Integer,List<Integer>> p:mp.entrySet()) {
            List<Integer> l=p.getValue();
            boolean f=true;
            for(int i:l) {
                for(int j:l) {
                    if(i!=j) {
                        if(adjm[i][j]!=1) {
                            f=false;
                            break;
                        }
                    }
                }
                if(f!=true) {
                    break;
                }
            }
            if(f==true) {
                ++ans;
            }
        }
        return ans;
    }
}
