class Solution {
    class Edge{
        int src;
        int dest;

        public Edge(int s, int d){
            this.src = s;
            this.dest = d;
        }
    }

    public int countCompleteComponents(int n, int[][] edges) {
        //create a graph
        List<Edge> graph[] = new ArrayList[n];
        for(int i=0; i<n; i++){
            graph[i] = new ArrayList<>();
        }
        for(int ed[] : edges){
            graph[ed[0]].add(new Edge(ed[0], ed[1]));
            graph[ed[1]].add(new Edge(ed[1], ed[0]));
        }

        boolean vis[] = new boolean[n];
        List<int[]> components = new ArrayList<>();
        //components at 0 will be edges and at 1 will be nodes

        for(int i=0; i<n; i++){
            int arr[] = new int[2];
            if(!vis[i]){                
                dfs(graph, vis, i, arr);
                components.add(arr); 
            }                 
        }

        int compo = 0; //calculate components using formula m*(m-1)/2
        for(int i=0; i<components.size(); i++){
            int temp[] = components.get(i);
            int edge = temp[1]/2;
            int nodes = temp[0];
            int cal = (nodes * (nodes-1))/2;
            if(edge == cal){
                compo++;
            }
        }
        return compo;
    }
    private void dfs(List<Edge> graph[], boolean vis[], int curr, int arr[]){
        vis[curr] = true;
        arr[0]++; //nodes increment
        for(int i=0; i<graph[curr].size(); i++){
            Edge e = graph[curr].get(i);
            arr[1]++; //edges increment
            if(!vis[e.dest]){                
                dfs(graph, vis, e.dest, arr);
            }            
        }
    }

}
