public class Solution {
    public int CountCompleteComponents(int n, int[][] edges) {
        // Get Adjacency List
        IList<IList<int>> adj = GetAdj(n, edges);
        int count = 0;

        // Stores Considered Nodes
        HashSet<int> considered = new HashSet<int>();

        for(int i=0; i<n; i++){
            if(considered.Contains(i)){
                continue;
            }

            HashSet<int> graphNodes = new();
            GetConnectedGraphNodes(adj, i, graphNodes);
            int edgesCount = graphNodes.Count - 1;
            bool isConnected = true;

            foreach(var node in graphNodes){
                considered.Add(node);

                if(adj[node].Count != edgesCount){
                    isConnected = false;
                }
            }

            if(isConnected){
                count++;
            }
        }

        return count;
    }

    private void GetConnectedGraphNodes(IList<IList<int>> adj, int currentNode, HashSet<int> graphNodes){
        if(graphNodes.Contains(currentNode)){
            return;
        }

        graphNodes.Add(currentNode);

        foreach(var childNode in  adj[currentNode]){
            GetConnectedGraphNodes(adj, childNode, graphNodes);
        }
    }

    private IList<IList<int>> GetAdj(int n, int[][] edges){
        IList<IList<int>> adj = new List<IList<int>>();

        for(int i=0; i<n; i++){
            adj.Add(new List<int>());
        }

        foreach(var edge in edges){
            adj[edge[0]].Add(edge[1]);
            adj[edge[1]].Add(edge[0]);
        }

        return adj;
    }
}
