class Solution {
public:
    void dfs(int node, vector<vector<int>>&adj, unordered_set<int>&st, vector<int>&vis)
    {
        vis[node] = 1;
        st.insert(node);

        for(auto j : adj[node])
        {
            if(!vis[j])
            {
                dfs(j, adj, st, vis);
            }
        }

        return;
    }
    int countCompleteComponents(int n, vector<vector<int>>& edges) {
        vector<vector<int>>adj(n);

        for(int i=0;i<edges.size();i++)
        {
            adj[edges[i][0]].push_back(edges[i][1]);
            adj[edges[i][1]].push_back(edges[i][0]);
        }

        vector<int>vis(n,0);
        int  ans = 0;
        for(int i=0;i<n;i++)
