            if(!vis[i])
            {
                bool flag = true;
                unordered_set<int>st;
                dfs(i,adj,st,vis);
                int sz = st.size()-1;
                for(auto k : st)
                {
                    if(adj[k].size() != sz)
                    {
                        flag = false;
                        break;
                    }
                }

                if(flag)ans++;

                st.clear();
            }
        }

        return ans;
    }
};
