```
class Solution {
public:
    pair<int,int> dfs(vector<int> adj[],vector<int>&vis,int node){
        vis[node]=1;
        int v=1,e=adj[node].size();
        for(int x:adj[node]){
            if(!vis[x]){
              pair<int,int> temp=  dfs(adj,vis,x);
              v+=temp.first;
              e+=temp.second;
            }
        }
        return {v,e};
    }
    int countCompleteComponents(int n, vector<vector<int>>& edges) {
      vector<int>adj[n];
      for(auto x:edges){
              adj[x[0]].push_back(x[1]);
              adj[x[1]].push_back(x[0]);
      }  
      vector<int>vis(n,0); 
      int count=0;
      for(int i=0;i<n;i++){
          if(!vis[i]){
            pair<int,int>tmp=  dfs(adj,vis,i);
            int ver=tmp.first;
            int edg=tmp.second;
            if(ver*(ver-1)==edg){
                count++;
            }
          }
      }
     return count;
    }
};
```

