class Solution {
public:
    void prepareList(unordered_map<int,set<int>>&adj,vector<vector<int>>& edges){

        for(int i = 0; i<edges.size();i++){
            int u = edges[i][0];
            int v = edges[i][1];

            adj[u].insert(v);
            adj[v].insert(u);
        }

    }
    void dfs(int i,unordered_map<int,bool>&visited,vector<int>&component,unordered_map<int,set<int>>&adj){
        if(visited[i]) return;

        visited[i] = true;
        component.push_back(i);

        for(auto &it: adj[i]){
            dfs(it,visited,component,adj);
        }

    }
    int countCompleteComponents(int n, vector<vector<int>>& edges) {
        unordered_map<int,set<int>>adj;
        unordered_map<int,bool>visited;

        prepareList(adj,edges);

        int count = 0;

        for(int i = 0; i<n;i++){
            if(!visited[i]){
                vector<int>nodes;

                dfs(i,visited,nodes,adj);
                int res = 0; 
                for(int j = 0; j<nodes.size();j++){
                    if(adj[nodes[j]].size() >= nodes.size()-1) res++;
                }

                if(res == nodes.size()) count++;

            }
        }
       return count; 
    }
};
```

