class Solution:
    def countCompleteComponents(self, n: int, edges: List[List[int]]) -> int:
        connections = {i: {i} for i in range(n)}
        for a, b in edges:
            connections[a].add(b)
            connections[b].add(a)
        nodes = [i for i in range(n)]
        nodes.sort(key=lambda i: -len(connections[i]))
        eliminated = set()
        count = 0
        for node in nodes:
            if node in eliminated:
                continue
            group = connections[node]
            if all(connections[element] == group for element in group):
                count += 1
            eliminated.update(group)
        return count
