class Solution {
public:
    void dfs(vector<int> adj[],int i,vector<int>&vis,vector<int>&vec)
    {
        vis[i]=1;
        vec.push_back(i);
        for(auto it:adj[i])
        {   
            if(!vis[it])
            {
                dfs(adj,it,vis,vec);
            }
        }
    }
    int countCompleteComponents(int n, vector<vector<int>>& edges) {
        vector<int> adj[n];
        for(int i=0;i<edges.size();i++)
        {
            adj[edges[i][0]].push_back(edges[i][1]);
            adj[edges[i][1]].push_back(edges[i][0]);
        }
        int cnt=0;
        vector<int>vis(n,0);
        vector<int>vec;
        for(int i=0;i<n;i++)
        {
            if(!vis[i])
            {
                dfs(adj,i,vis,vec);
                int flag=0;
                int sz=vec.size()-1;
                for(int j=0;j<vec.size();j++)
                {
                   if(sz!=adj[vec[j]].size())
                   {
                       flag=1;
                       break;
                   }                    
                }
                if(!flag)
                cnt++;
                vec.clear();
            }
        }
        return cnt;
    }
};
