use std::collections::*;

struct UnionFind {
  parents: Vec<usize>,
  sizes: Vec<usize>,
  ranks: Vec<usize>
}

impl UnionFind {
  fn new(n:usize) -> UnionFind {
    let mut uf = UnionFind { parents: vec![0;n], sizes:vec![1;n], ranks:vec![0;n] };
    for i in 0..n {
      uf.parents[i] = i;
    }
    uf
  }

  fn root(&mut self, x:usize) -> usize {
    let ti = self.parents[x];
    if ti == x {
      x
    } else {
      self.parents[x] = self.root(ti);
      self.parents[x]
    }
  }

  fn is_same(&mut self, x:usize, y:usize) -> bool {
    self.root(x) == self.root(y)
  }

  fn unite(&mut self, x:usize, y:usize) -> bool {
    let rx = self.root(x);
    let ry = self.root(y);
    if rx == ry { return false }
    let (rx, ry) = if self.ranks[rx] < self.ranks[ry] {
      (ry, rx)
    } else {
      (rx, ry)
    };
    self.parents[ry] = rx;
    if self.ranks[rx] == self.ranks[ry] {
      self.ranks[rx] += 1;
    }

    self.sizes[rx] += self.sizes[ry];
    true
  }

  fn group_size(&mut self, x:usize) -> usize {
    let i = self.root(x);
    self.sizes[i]
  }
}

impl Solution {
  pub fn count_complete_components(n: i32, edges: Vec<Vec<i32>>) -> i32 {
    let n = n as usize;
    let mut g = vec![vec![];n];
    let mut tree = UnionFind::new(n);

    for arr in &edges {
      let a = arr[0] as usize;
      let b = arr[1] as usize;

      g[a].push(b);
      g[b].push(a);
      tree.unite(a, b);
    } 

    let mut map = HashMap::new();
    for i in 0..n {
      let pi = tree.root(i);
      map.entry(pi).or_insert(vec![]).push(i);
    }

    let mut result = 0;
    for (_, nodes) in map {
      let need_edge_num = nodes.len() - 1;
      let mut success = true;
      for i in nodes {
        if g[i].len() != need_edge_num {
          success = false;
          break
        }
      }
      if success {
        result += 1;
      }
    }

    result
  }
}
```

