class Solution:
    def countCompleteComponents(self, n: int, edges: List[List[int]]) -> int:

        parent = {}
        def find_parent(i):
            if i not in parent or parent[i] == i:
                parent[i] = i
                return i 

            parent[i] = find_parent(parent[i])
            return parent[i]
        in_degree = collections.defaultdict(int)
        for a , b in edges:
            p_a = find_parent(a)
            p_b = find_parent(b)
            in_degree[b] +=1
            if p_a != p_b:
                parent[p_a] = p_b

        group = collections.defaultdict(set)
        for i in range(n):
            p = find_parent(i)
            group[p].add(i)
        ans = 0
        for p in group:
            edges = 0
            for n in group[p]:
                edges += in_degree[n]
            if edges == comb(len(group[p]), 2):
                ans +=1        
        return ans

