class Solution {
public:

long long change_to(string s,char from,char to)
{
    long long n = s.size(),count = 0,ans = 0;
    int i = 0;
    while(i<n)
    {
        while(i<n && s[i]==from)
        {
            i++;
            count++;
        }
        if((s[i]==to || i>=n-1)&& count>0)
        {
            int rem = i-count;
            ans = ans+i+rem;
            count = 0;
        }
        i++;
    }
    return ans;
}
long long minimumCost(string s)
   {
       long long n = s.size(),ans1 = 0,ans2 = 0;
       long long len = (n)/2;
       string s1 = s.substr(0,len),s2 = s.substr(n%2==0 ? n/2 : n/2+1,len);
       reverse(s2.begin(),s2.end());

        ans1 = change_to(s1,'0','1')+change_to(s2,'0','1');
        ans2 = change_to(s1,'1','0')+change_to(s2,'1','0');

        if(n%2!=0 && s[n/2]=='1') ans2+=n;
        if(n%2!=0 && s[n/2]=='0') ans1+=n;

        return min(ans1,ans2);
    }
};
```

