class Solution {
public:

    long long firstHalf(string &s , char k){
        long long ans = 0;
        int n = s.length();
        bool flip = false;
        for(int i=n-1;i>=0;i--){
            if(s[i] == k){
                if(flip){
                    ans += i+1;
                    flip = false;
                }
            }
            else{
                if(!flip){
                    ans += i+1;
                    flip = true;
                }
            }
        }
        return ans;
    }
    long long secondHalf(string &s , char k, int l, int mid){
        long long ans = 0;
        int n = s.length();
        bool flip = false;

        for(int i=0;i<n;i++){
            if(s[i] == k){
                if(flip){
                    ans += (l - (mid+i));
                    flip = false;
                }
            }
            else{
                if(!flip){
                    ans += (l - (mid + i));
                    flip = true;
                }
            }
        }
        return ans;
    }

    long long minimumCost(string s) {
        int n = s.length();

        int mid = n/2;
        string s1 = s.substr(0,mid);
        string s2 = s.substr(mid, n-mid);
        // cout << s1 << " " << s2 << endl;
        long long a = firstHalf(s1, '0');
        long long b = secondHalf(s2, '0', n , mid);
        // cout << a << " " << b << endl;
        long long c = firstHalf(s1, '1');
        long long d = secondHalf(s2, '1', n , mid);
        // cout << c << " " << d << endl;
        return min(a+b , c+d);
    }
};
```

