class Solution {

public:
    long long minimumCost(string s) {
        int n = s.length();
        int k = n/2; // to split in half

        // all zero
        vector<char> to = {'0', '1'}; 
        long long res1 = 0; 
        for(int i = k-1; i>=0; i--){
            if(s[i] != to[0]){
                res1 += i+1; 
                swap(to[0], to[1]); 
            }
        }
        to = {'0', '1'};  
        for(int i = k; i<n; i++){
            if(s[i] != to[0]){
                res1 += n-i; 
                swap(to[0], to[1]); 
            }

        }

        // all one
        to = {'1', '0'};
        long long res2 = 0; 
        for(int i = k-1; i>=0; i--){
            if(s[i] != to[0]){
                res2 += i+1; 
                swap(to[0], to[1]); 
            }
        }
        to = {'1', '0'};
        for(int i = k; i<n; i++){
            if(s[i] != to[0]){
                res2 += n-i; 
                swap(to[0], to[1]); 
            }

        }
        return min(res1, res2);
    }
};
```

