class Solution {
public:
    template <typename I>
    long long fix(I first, I last, bool make_zero) {
        long long cost = 0;
        for ( ; first != last; ++first) {
            if ((*first == '0') != make_zero) {
                cost += static_cast<long long>(last - first);
                make_zero = !make_zero;
            }
        }
        return cost;
    }

    long long minimumCost(string s) {
        auto mid = begin(s) + s.size() / 2;
        return fix(make_reverse_iterator(mid), rend(s), *mid == '0') +
               fix(mid, end(s), *mid == '0');
    }
};
```

