            else if(c == v && flip % 2 != 0){
                ans += len - i;
                flip++;
            }
        }
        return ans;
    }
    public long minimumCost(String s) {
        long ans0 = helper(s,0);
        long ans1 = helper(s,1);
        return Math.min(ans0,ans1);
    }
}
