class Solution:
  def minimumCost(self, s: str) -> int:
    dp, n = [0] * len(s), len(s)
    for i in range(1, n):
      dp[i] = dp[i - 1] + (i if s[i - 1] != s[i] else 0)

    res, cnt = dp[-1], 0
    for i in range(n - 2, -1, -1):
      cnt += n - 1 - i if s[i] != s[i + 1] else 0
      res = min(res, dp[i] + cnt)

    return res
