        vector<vector<long long>> prefixDp(s.size()+1,{0,0});
        for(int i=1;i<=sz;i++){
            if (s[i-1]=='0'){
                prefixDp[i][0]=min(prefixDp[i-1][0],prefixDp[i-1][1]+i-1);
                prefixDp[i][1]=min(prefixDp[i-1][0]+i,prefixDp[i-1][1]+i+i-1);
