impl Solution {
    pub fn minimum_cost(s: String) -> i64 {

        //dp 
        let s = s.chars().into_iter().collect::<Vec<char>>();

        let n = s.len();
        let mut dp1 = vec![vec![0i64; 2]; n];//minimum cost to convert 0 or 1 till 0..i 
        let mut dp2 = vec![vec![0i64; 2]; n];//minimum cost to convert 0 or 1 till i from right

        for i in 0..n{

            let one_cost = if s[i]=='1' {0} else {1};
            let zero_cost = 1-one_cost ; 

            if i>0{
                if s[i]=='0'{
                    dp1[i][0] = dp1[i-1][0];
                }else{
                    dp1[i][0] = dp1[i-1][1]+ i as i64+1;
                }

                if s[i]=='1'{
                    dp1[i][1] = dp1[i-1][1];
                }else{
                    dp1[i][1] = dp1[i-1][0]+ i as i64+1;
                }
            }else{
                dp1[0][0] = zero_cost;
                dp1[0][1] = one_cost;
            }
        }

        println!("{:?}", dp1);
        let mut ans: i64 =  dp1[n-1][0].min(dp1[n-1][1]);

        for i in (0..n).rev(){
            let one_cost = if s[i]=='1' {0} else {1};
            let zero_cost = 1-one_cost ; 

            if i<n-1{
                if s[i]=='0'{
                    dp2[i][0] = dp2[i+1][0];
                }else{
                    dp2[i][0] = dp2[i+1][1]+ (n-i) as i64;
                }

                if s[i]=='1'{
                    dp2[i][1] = dp2[i+1][1];
                }else{
                    dp2[i][1] = dp2[i+1][0]+ (n-i) as i64;
                }
            }else{
                dp2[i][0] = zero_cost;
                dp2[i][1] = one_cost;
            }

        }

        ans = ans.min(dp2[0][0].min(dp2[0][1]));

        for i in 0..n{
            if i+1<n{
                ans = ans.min(dp1[i][0]+dp2[i+1][0]);
                ans = ans.min(dp1[i][1]+dp2[i+1][1]);
            }

        }
        ans


    }
}
