        int n = s.length();

        string one = "";
        string zero = "";

        for (int i = 0; i < n; i++) {
            one.push_back('1');
            zero.push_back('0');
        }

        if (s == one || s == zero) {
            return 0;
        }

        vector<vector<long long>> pre(2, vector<long long>(n, 0));
        vector<vector<long long>> suf(2, vector<long long>(n, 0));

        for (int i = 0; i < n; i++) {
            if (i == 0) {
                pre[0][i] = s[i] == '0' ? 0 : 1;
                pre[1][i] = s[i] == '1' ? 0 : 1;
            } else {
                pre[0][i] = s[i] == '0' ? pre[0][i - 1] : i + 1 + pre[1][i - 1];
                pre[1][i] = s[i] == '1' ? pre[1][i - 1] : i + 1 + pre[0][i - 1];
            }

            int j = n - i - 1;
            if (j == n - 1) {
                suf[0][j] = s[j] == '0' ? 0 : 1;
                suf[1][j] = s[j] == '1' ? 0 : 1;
            } else {
                suf[0][j] = s[j] == '0' ? suf[0][j + 1] : n - j + suf[1][j + 1];
                suf[1][j] = s[j] == '1' ? suf[1][j + 1] : n - j + suf[0][j + 1];
