class Solution {
public:
    long long minimumCost(const string& s)
    {
        if(s.size() < 2) return 0;

        const int half = s.size()/2;
        long long cost = 0;

        for(size_t i = 1; i <= half; ++i)
            if(s[i] != s[i - 1])
                cost += i;

        for(size_t i = s.size() - 2; i >= half; --i)
            if(s[i] != s[i + 1])
                cost += s.size() - 1 - i;

        return cost;

    }
};
