class Solution {
    public long minimumCost(String s) {

        boolean flip = false;
        long res = 0l, n = s.length();
        for(int i = 1; i<n; i++){
            char left = s.charAt(i-1);
            char curr = s.charAt(i);
            if(flip){
                if(left == '0') left = '1';
                else left = '0';

                if(curr == '0') curr = '1';
                else curr = '0';
            }

            if(left != curr)
            if(i <= n-i){
                res += i;

            }
            else{
                res += (n-i);
                flip = !flip;
            }

        }

        return res;

    }
}
