class Solution:
    def minimumCost(self, S: str) -> int:
        def count(s):
            Z = [0]
            O = [0]
            for i,l in enumerate(s):
                if l == '1':
                    O.append(O[-1])
                    Z.append(O[-1] + i + 1)
                else:
                    Z.append(Z[-1])
                    O.append(Z[-1] + i + 1)

            return Z[1:],O[1:]

        LZ, LO = count(S)
        RZ, RO = count(S[::-1])
        ans = inf
        for x,y in zip(LZ,RZ[::-1]):
            ans = min(ans, x + y)

        for x,y in zip(LO,RO[::-1]):
            ans = min(ans, x + y)

        return ans

