class Solution {

    public long minimumCost(String s) {

        long ans = 0;

        for (int i = 1 ; i < s.length () ; i++) {

            // Left main kitne flip karne hain aur right main kitne flip karne hain.
            // Inme se jo minimum flip lagengye unhe ans main add kardo.        
            if (s.charAt(i) != s.charAt(i-1)) {

                ans += Math.min (i , s.length() - i);
            }
        }

        return ans;

    }
}
