class Solution {
public:
    #define ll long long 
    ll solve(string s,int st,int end,int type,char val){
        ll ans = 0 ; 
        if(type == 0){
            int x = 0 ; 
            for(int i = end;i>=st;i--){
                if(x%2 != 0){
                    if(s[i]=='1')s[i]='0';
                    else s[i]='1';
                }
                if(s[i]!= val){
                    x++;
                    ans = ans + i+1;
                }
            }
        }
        if(type == 1){
            int x = 0 ; 
            for(int i = st;i<=end ; i++){
                if(x%2 != 0){
                    if(s[i]=='1')s[i]='0';
                    else s[i]='1';
                }
                if(s[i]!= val){
                    x++;
                    ans = ans + s.size()-i;
                }
            }
        }
        return ans;
    }
    long long minimumCost(string s) {
        int n = s.size();
        int mid = n/2;
        ll zf = solve(s,0,mid,0,'0');
        ll zs = solve(s,mid+1,n-1,1,'0');
        ll of = solve(s,0,mid,0,'1');
        ll os =  solve(s,mid+1,n-1,1,'1');
        return min(zs+zf,os+of);

    }
};

