class Solution {
  public long minimumCost(String s) {
    long fl0 = to_left(s,0);
    long fl1 = to_left(s,1);
    long fr0 = to_right(s, 0);
    long fr1 = to_right(s, 1);

    long mini0 = fl0 + fr0, mini1 = fl1 + fr1;  
    if( (s.length() & 1) == 0) return Math.min(mini0, mini1);

    int k = s.length()>>1, k0, k1;
    if(s.charAt(k) == '0') mini1 = Math.min(k + fl1 + fr0, k + fl0 + fr1);
    else                   mini0 = Math.min(k + fl1 + fr0, k + fl0 + fr1);

    return Math.min(mini0, mini1);
  }

  private long to_left(String s, int mark){
    long flip = 0;
    for(int i = (s.length()>>1) - 1; i >= 0; --i)
      if(s.charAt(i) - '0' != mark){
        flip += i+1;
        mark ^= 1;
      }

    return flip;
  }
  private long to_right(String s, int mark){
    long flip = 0;
    for(int i = (s.length()+1)>>1; i != s.length(); ++i)
      if(s.charAt(i) - '0' != mark){
        flip += s.length() - i;
        mark ^= 1;
      }

    return flip;
  }
}
