class Solution {
    using ll = long long;
public:
    long long minimumCost(string s) {
        int n = size(s);
        vector<ll> prefix(n);
        vector<ll> suffix(n);
        //prefix[i] represent the cost required to make all the charecters from 0th to (i-1)th index equal to the charecter at ith charecter 
        for(int i=0;i<n;i++){
            if(i == 0){
                prefix[i] = 0;
            }
            else{
                if(s[i] == s[i-1]){
                    prefix[i] = prefix[i-1];
                }
                else if(s[i]!=s[i-1]){
                    prefix[i] = i + prefix[i-1];
                }
            }
        }
        for(int i=n-1;i>=0;i--){
            if(i == n-1){
                suffix[i] = 0;
            }
            else{
                if(s[i] == s[i+1]){
                    suffix[i] = suffix[i+1];
                }
                else{
                    suffix[i] = (n-i-1) + suffix[i+1];
                }
            }
        }
        ll ans = LONG_LONG_MAX;
        for(int i=0;i<n;i++){
            ans = min(ans,prefix[i] + suffix[i]);
        }
        return ans;
    }
};
```

