class Solution {
public:
    long long dp[100001][2][2];
    int n;
    long long f(int ind, int prev, string &s, bool inverted){
        if(ind >= n) return 0;
        if(dp[ind][prev][inverted] != -1)
            return dp[ind][prev][inverted];

        //if curr and prev match and inverted is false
        if(s[ind] - 48 == prev && !inverted)
            return dp[ind][prev][inverted]= f(ind+1, s[ind]-48, s,false);
        //if prev and curr match and inverted is true.. then its considered as mismatch
        else if(s[ind]-48 == prev and inverted)
            return dp[ind][prev][inverted]= min(ind+ f(ind+1,s[ind]-48,s,false), n-ind + f(ind+1,!(s[ind]-48), s,true));
       //if prev and curr not match and inverted is true.. then its considered as match
        else if(s[ind] - 48 != prev and inverted)
            return dp[ind][prev][inverted]= f(ind+1, s[ind]-48, s,false);
        //if prev and curr not match and inverted is false.. then its considered as mismatch
        else if(s[ind] - 48 != prev and !inverted)
            return dp[ind][prev][inverted]= min(ind+ f(ind+1,s[ind]-48,s,false), n-ind + f(ind+1,!(s[ind]-48), s,true));
        else return -1;
    }
    long long minimumCost(string s) {
        n= s.size();
        memset(dp,-1,sizeof(dp));
        return f(1,s[0]-48,s,false); //ind,prev,string,inverted
    }
};

