class Solution {
public:
    typedef long long ll;
    long long minimumCost(string s) {
        ll ans=1e18;
        int n= s.size();
        vector<ll> pre(n,0),suf(n,0);
        pre[0]=0;
        suf[n-1]=0;
        for(int i=1;i<n;++i)
           pre[i]= pre[i-1] + (s[i] != s[i-1] ? i : 0);
        for(int i=n-2;i>=0;--i)
            suf[i]= suf[i+1] + (s[i] != s[i+1] ? (n-i-1) : 0);

        for(int i=0;i<n;i++)
            ans= min(ans, pre[i]+suf[i]);
        return ans;
    }
};

