class Solution {
public:
    long long minimumCost(string s) {
        long long ans1 = 0,ans2=0;
        int n = s.size();
        for(int i=0;i<n;i++)
        {
            if(s[i]=='1')
            {
                long long tmp1 = i;
                int idx = i;
                while(i<n && s[i]=='1')
                    i++;
                tmp1+=i;
                long long tmp2 = n-idx+(n-i);
                ans1+=min(tmp1,tmp2);
            }
        }
        for(int i=0;i<n;i++)
        {
            if(s[i]=='0')
            {
                long long tmp1 = i;
                int idx = i;
                while(i<n && s[i]=='0')
                    i++;
                tmp1+=i;
                long long tmp2 = n-idx+(n-i);
                ans2+=min(tmp1,tmp2);
            }
        }
        return min(ans1,ans2);
    }
};
