class Solution {
public:
    long long solve(string &s, int x, int count, long long**dp){
        if(x>=s.size())
            return 0;
        else if(dp[x][count]!=-1)
            return dp[x][count];
        long long ans=0;
        if(count==0){
            if(s[x]!=s[x-1])
                ans=ans+x;
            ans=ans+min(solve(s,x+1,1,dp),solve(s,x+1,0,dp));
        }else{

            if(s[x]!=s[x-1])
                ans=ans+s.size()-x;
            ans= ans+solve(s,x+1,1,dp);

        }
        dp[x][count]=ans;
         return ans;
    }
    long long minimumCost(string s) {
        int n=s.size();
    long long** dp = new long long*[n];

    for (int i = 0; i < n; i++) {
        dp[i] = new long long[2];
        for(int j=0;j<2;j++)
            dp[i][j]=-1;
    }

       return solve(s,1,0,dp);
    }
};
